/**是否是electron环境*/
function isElectron(){
    var sUserAgent = navigator.userAgent.toLowerCase();
    return sUserAgent.indexOf("electron")>-1;
}

/**是否是服务器远程运行环境*/
function isServer(){
    return window.location.host!="";
}
